import React, { useState, useEffect, useCallback } from 'react';
import { 
  List, 
  ListItem, 
  ListItemText, 
  Checkbox, 
  Button, 
  Box, 
  Typography, 
  CircularProgress 
} from '@mui/material';
import { getTasks, deleteTask, updateTask } from '../api';

const TaskList = ({ projectId, refreshTrigger }) => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchTasks = useCallback(async () => {
    try {
      setLoading(true);
      const response = await getTasks(projectId);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    fetchTasks();
  }, [fetchTasks, refreshTrigger]);

  const handleDelete = async (taskId) => {
    try {
      const response = await deleteTask(projectId, taskId);
      if (response.status === 200) {
        // Optimistically update the UI
        setTasks(prevTasks => prevTasks.filter(task => task.id !== taskId));
      }
    } catch (error) {
      console.error('Error deleting task:', error);
      // Show error to user
      alert(`Failed to delete task: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleToggleComplete = async (task) => {
    try {
      await updateTask(projectId, task.id, { completed: !task.completed });
      fetchTasks(); // Refresh after update
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  if (loading) {
    return <CircularProgress />;
  }

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Tasks
      </Typography>
      {tasks.length === 0 ? (
        <Typography variant="body1" color="textSecondary">
          No tasks yet. Create your first task!
        </Typography>
      ) : (
        <List>
          {tasks.map((task) => (
            <ListItem key={task.id} divider>
              <Checkbox
                checked={task.completed}
                onChange={() => handleToggleComplete(task)}
              />
              <ListItemText
                primary={task.title}
                secondary={task.description}
                sx={{ 
                  textDecoration: task.completed ? 'line-through' : 'none',
                  color: task.completed ? 'text.secondary' : 'text.primary'
                }}
              />
              <Button
                variant="outlined"
                color="error"
                onClick={() => handleDelete(task.id)}
                size="small"
              >
                Delete
              </Button>
            </ListItem>
          ))}
        </List>
      )}
    </Box>
  );
};

export default TaskList;
